   protected URI findVAppURIInArgsOrNull(GeneratedHttpRequest<?> gRequest) {
      for (Object arg : gRequest.getArgs()) {
         if (arg instanceof URI) {
            return (URI) arg;
         } else if (arg instanceof FirewallRule[]) {
        	 URI[] rules = (URI[]) arg;
            return (rules.length > 0) ? rules[0] : null;
         }
      }
      return null;
   }

